//DOM
const filePicker = document.getElementById("image"); // refer to input id //img input
const imagePreview = document.getElementById("image-preview"); //img display

//functions
function showPreview() {
  const images = filePicker.files;

  if (!images || images.length === 0) {
    imagePreview.style.display = "none"; // close preview
    return;
  }

  const pickedImg = images[0]; //only grab the current img

  // parse filePicker.files into a link
  // file is not uploaded yet
  // URL is not generated by the server yet
  imagePreview.src = URL.createObjectURL(pickedImg);
  imagePreview.style.display = "block";
}

//listeners
filePicker.addEventListener("change", showPreview);

